menu={}
local items={}
local ind=1
load={}

local function getFileList()
    local names = {}
    local files = love.filesystem.getDirectoryItems("")
    for k,file in ipairs(files) do
        local name = file:match("^(.+)%.chp$")
        if name then
            table.insert(names,tostring(name))
        end
    end
    return names
end

