function menu:enter()
    local isFile = love.filesystem.getInfo("TextDemo.chp")
    if not isFile then
        love.filesystem.write("TextDemo.chp", demo)
    end
    items=getFileList()
    love.filesystem.write("DONTREADME.txt", "this is a temporary file just ignore this")
    
    initFont("assets/font.png","abcdefghijklmnopqrstuvwxyz !CS0123456789.:(){}",7,8)
    input2=baton.new {
        controls = {
            left = {'key:left', 'key:a', 'axis:leftx-', 'button:dpleft'},
            right = {'key:right', 'key:d', 'axis:leftx+', 'button:dpright'},
            up = {'key:up', 'key:w', 'axis:lefty-', 'button:dpup'},
            down = {'key:down', 'key:s', 'axis:lefty+', 'button:dpdown'},
            a={'key:z', "button:a"},
            b={'key:x', "button:b"},
            c={'key:c', "button:y"},
            pause={'key:return','button:start'},
            exit={'button:x'}
          },
          joystick = love.joystick.getJoysticks()[1],

    }
end

function menu:update()
    input2:update()
    if input2:pressed("down") then
        ind=ind+1
        if ind>#items then ind=1 end
    end
    if input2:pressed("up") then
        ind=ind-1
        if ind<1 then ind=#items end
    end
    if input2:pressed("a") then
        load.readFile(items[ind])
        name=items[ind]
        gs.switch(runCart)
    end
end

function menu:draw()
    for k=1,#items do
        if k==ind then
            lg.setColor(1,1,1)
        else
            lg.setColor(.5,.5,.5)
        end
        lg.print(items[k],8,k*12)
    end
    --drawFont("hello world",0,0)
    --drawChar("a",0,0)
    --lg.print(tostring(string.find(font.text,"a")))
    --lg.print(font.text)
end

name=""

function menu:pressed(key)
    if key=="up" then
        ind=ind-1
        if ind<1 then ind=#items end
    end
    if key=="down" then
        ind=ind+1
        if ind>#items then ind=1 end
    end
    if key=="return" or key=="z" then
        load.readFile(items[ind])
        name=items[ind]
        gs.switch(runCart)
    end
end

return menu